// 该文件用于创建Vuex中最为核心的store

//引入vue
import { values } from 'core-js/core/array';
import Vue from 'vue';
// 引入Vuex
import Vuex from 'vuex';
//使用vuex
Vue.use(Vuex);


//准备actions--用于响应组件中的动作
const actions = {
    jia(context,value){
        console.log('action里的jia被调用了',context,values);
        context.commit('jia',value)
    }
};
//准备mutations--用于操作数据(state)
const mutations = {};
//准备state--用于存储数据
const state = {
    sum: 0,  //当前的和
};

//创建并暴露store
export default new Vuex.Store({
    actions,
    mutations,
    state,
});
